class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        vector<vector<pair<long long,int>>> dp(n,vector<pair<long long,int>> (3,{0,0}));
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>pq;
        for(int i=0;i<n;i++){
           pq.push({board[i][0],0});
           pq.push({board[i][1],1});
           pq.push({board[i][2],2});
           for(int j=3;j<board[i].size();j++){
            pq.push({board[i][j],j});
            pq.pop();
           }

           for(int j=0;j<3;j++){
            long long val=pq.top().first;
            int ind=pq.top().second;
            dp[i][j]={val,ind};
            pq.pop();
           } 
        }
        long long ans=-4e9;
        for(int i=0;i<n-2;i++){

            for(int a=0;a<3;a++){
                int x=dp[i][a].second;
            for(int j=i+1;j<n-1;j++){
                for(int b=0;b<3;b++){
                    int y=dp[j][b].second;
                if(x!=y){
                 for(int k=j+1;k<n;k++){
                   for(int c=0;c<3;c++){
                       if(dp[k][c].second!=x&&dp[k][c].second!=y){
                           ans=max(ans,dp[i][a].first+dp[j][b].first+dp[k][c].first);
                       }
                   }
                 }
                }
                }
            }
            }
        }

        return ans;

    }
};
