class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long S = LLONG_MIN;
        vector<vector<long long>> a(m, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> b(m, vector<int>(3, -1));

        for (int i = 0; i < m; ++i) {
            vector<pair<long long, int>> c;
            for (int j = 0; j < n; ++j) {
                c.emplace_back(board[i][j], j);
            }
            sort(c.rbegin(), c.rend());

            for (int k = 0; k < 3 && k < n; ++k) {
                a[i][k] = c[k].first;
                b[i][k] = c[k].second;
            }
        }

        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (b[r2][j] == b[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (b[r3][k] == b[r1][i] || b[r3][k] == b[r2][j]) continue;

                                long long currentSum = a[r1][i] + a[r2][j] + a[r3][k];
                                S = max(S, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return S;
    }
};
