import java.util.*;

public class Solution {
    public long maximumValueSum(int[][] board) {
        int numRows = board.length;
        int numCols = board[0].length;
        long maxSum = Long.MIN_VALUE;
        long[][] topValues = new long[numRows][3];
        int[][] topColumns = new int[numRows][3];
        for (int row = 0; row < numRows; ++row) {
            List<Pair> columnValues = new ArrayList<>();
            for (int col = 0; col < numCols; ++col) {
                columnValues.add(new Pair(board[row][col], col));
            }
            columnValues.sort((a, b) -> Long.compare(b.value, a.value));

            for (int rank = 0; rank < 3 && rank < numCols; ++rank) {
                topValues[row][rank] = columnValues.get(rank).value;
                topColumns[row][rank] = columnValues.get(rank).column;
            }
        }
        for (int row1 = 0; row1 < numRows; ++row1) {
            for (int row2 = row1 + 1; row2 < numRows; ++row2) {
                for (int row3 = row2 + 1; row3 < numRows; ++row3) {
                    for (int col1 = 0; col1 < 3; ++col1) {
                        for (int col2 = 0; col2 < 3; ++col2) {
                            if (topColumns[row2][col2] == topColumns[row1][col1]) continue;
                            for (int col3 = 0; col3 < 3; ++col3) {
                                if (topColumns[row3][col3] == topColumns[row1][col1] || topColumns[row3][col3] == topColumns[row2][col2]) continue;

                                long currentSum = topValues[row1][col1] + topValues[row2][col2] + topValues[row3][col3];
                                maxSum = Math.max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
    private static class Pair {
        long value;
        int column;

        Pair(long value, int column) {
            this.value = value;
            this.column = column;
        }
    }
}
