class Solution {
public:
    #define ll long long
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<ll>>v;//val, row, col
        int m = board.size(), n = board[0].size();
        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                v.push_back({board[i][j], i, j});
            }
        }
        sort(v.rbegin(), v.rend());

        ll ans = LLONG_MIN;
        int cnt = 0;
        for(int i = 0; i < v.size(); i++)
        {
            for(int j = i + 1; j < v.size(); j++)
            {
                if(v[i][1] == v[j][1] || v[i][2] == v[j][2])continue;
                for(int k = j + 1; k < v.size(); k++)
                {
                    if(v[k][1] == v[j][1] || v[k][1] == v[i][1] || v[k][2] == v[i][2] || v[k][2] == v[j][2])continue;
                    ll cur = v[i][0] + v[j][0] + v[k][0];
                    if(cur > ans)ans = cur;
                    cnt++;
                    if(cnt >= 10000000)break;
                }
                if(cnt >= 10000000)break;
            }
            if(cnt >= 10000000)break;
        }
        return ans;
    }
};
