class Solution {
public:

    using ll = long long;
    using pi = pair<ll, ll>;
    ll max(ll a, ll b){return a > b?a:b;}
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        ll res = -1e12;

        vector<int> t[m+5];
        for(ll i = 0; i < 2; i++) {
            vector<pi> st;
            for(ll j = 0; j < n; j++) {
                st.push_back({board[j][i], j});
            }
            sort(st.begin(), st.end());
            int len = st.size();
            for(int it = len - 1; it >=  len - 3; it--) {
                t[i].push_back(st[it].second);
            }
        }
        for(int i = 2; i < m; i++) {
            vector<pi> st;
            for(int j = 0; j < n; j++) {
                st.push_back({board[j][i], j});
            }
            sort(st.begin(), st.end());
            int len = st.size();
            for(int it = len - 1; it >=  len - 3; it--) {
                t[i].push_back(st[it].second);
            }
            for(int j = i - 1; j >= 1; j--) {
                for(int k = j - 1; k >= 0; k--) {
                    for(auto h: t[i]) {
                        for(auto l: t[j]) {
                            if(l == h) continue;
                            for(auto d: t[k]) {
                                if(d == h || d== l) continue;
                                res = max(res, (ll)board[h][i] + board[l][j] + board[d][k]);                            
                            }
                        }
                    }
                }
            }
        }
         return res;
    }
};
