from collections import Counter
class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        nodes = []
        for i,row in enumerate(board):
            for j,x in enumerate(row):
                nodes.append((x,i,j))
        nodes.sort(reverse = True)
        n = len(nodes)
        ans = -float('inf')
        for i in range(0,n-2):
            x,i_,j_ = nodes[i]
            if ans!=-float('inf') and 3*x<=ans:
                break
            rowcounter,colcounter = Counter(),Counter()
            rowcounter[i_]+=1
            colcounter[j_]+=1
            res = x
            for j in range(i+1,n-1):
                x1,i_,j_ = nodes[j]
                if ans!=-float('inf') and 2*x<=ans-res:
                    break
                if rowcounter[i_]>0 or colcounter[j_]>0:
                    continue
                res+=x1
                rowcounter[i_]+=1
                colcounter[j_]+=1
                for k in range(j+1,n):
                    x2,i__,j__ = nodes[k]
                    if ans!=-float('inf') and x<=ans-res:
                        break
                    if rowcounter[i__]>0 or colcounter[j__]>0:
                        continue
                    res+=x2
                    ans = max(res,ans)
                    res-=x2
                res-=x1
                rowcounter[i_]-=1
                colcounter[j_]-=1
        return ans
