class Solution {
public:
    long long solve(vector<vector<int>>& b, vector<pair<int,int>> c)
    {
        long long ans = LONG_LONG_MIN;
        for(int i = 0; i < c.size(); i++)
        {
            for(int j = i+1; j < c.size(); j++)
            {
                for(int k = j+1; k < c.size(); k++)
                {
                    if(c[i].first!=c[j].first&&c[k].first!=c[j].first&&c[i].first!=c[k].first&&c[i].second!=c[j].second&&c[k].second!=c[j].second&&c[i].second!=c[k].second)
                    {
                        long long temp = b[c[i].first][c[i].second];
                        temp+=b[c[j].first][c[j].second];
                        temp+=b[c[k].first][c[k].second];
                        ans = max(ans, temp);
                    }
                }
            }
        }
        return ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<int,int>>> v(n);
        for(int i = 0; i < n; i++)
        {
            vector<pair<int,pair<int,int>>> vp;
            for(int j = 0; j < m; j++)
            {
                vp.push_back({board[i][j],{i,j}});
            }
            sort(vp.begin(),vp.end());
            v[i].push_back(vp[m-1].second);
            v[i].push_back(vp[m-2].second);
            v[i].push_back(vp[m-3].second);
        }
        long long ans = LONG_LONG_MIN;
        for(int i = 0; i < n; i++)
        {
            for(int j = i+1; j < n; j++)
            {
                for(int k = j+1; k < n; k++)
                {
                    vector<pair<int,int>> choice;
                    choice.insert(choice.end(),v[i].begin(),v[i].end());
                    choice.insert(choice.end(),v[j].begin(),v[j].end());
                    choice.insert(choice.end(),v[k].begin(),v[k].end());
                    ans=max(ans,solve(board,choice));
                }
            }
        }
        return ans;
    }
};
