typedef long long ll ; 
class Solution {
public:
     vector<pair<ll,ll>> f , s , t  ;
    vector<vector<int>> board ; 


    ll get(ll i , ll j , ll k)
    {

        ll col1 = f[i].second ; 

        ll col2 = -1 ; 
        if(f[j].second!=col1)
            col2 = f[j].second  ; 
        else col2 = s[j].second ; 

        ll col3 = -1; 
        if(f[k].second!=col1 and f[k].second!=col2)
            col3 = f[k].second ; 
        else   if(s[k].second!=col1 and s[k].second!=col2)
            col3 = s[k].second ; 
        else col3 = t[k].second ; 


        ll tans = board[i][col1] ;
        tans += board[j][col2];
        tans += board[k][col3] ; 
        return tans ; 
    }

    long long maximumValueSum(vector<vector<int>>& v) {
        f.clear();
        s.clear();
        t.clear();
        board = v ; 

        for(auto & i : board)
        {
            vector<pair<ll,ll>> temp ; 
            for(int j = 0 ; j<i.size();++j)
                temp.push_back({i[j],j});
            sort(temp.begin(),temp.end());
            reverse(temp.begin(),temp.end());

            f.push_back(temp[0]);
            s.push_back(temp[1]);
            t.push_back(temp[2]);
        }

        ll ans = -1e12; 
        ll n = board.size(); 
        for(ll i = 0 ; i<n; ++i)
        {
            for(ll j = i+1 ; j<n; ++j)
            {
                for(ll k = j+1 ; k<n; ++k)
                {



                    ans = max(ans , get(i,j,k));
                     ans = max(ans , get(i,k,j));
                     ans = max(ans , get(j,i , k));
                     ans = max(ans , get(j,k,i));
                     ans = max(ans , get(k,i,j));
                     ans = max(ans , get(k,j,i));
                }
            }
        }

        return ans ; 

    }
};
