class Solution {
public:
const long long inf = 1e10;
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size(),m=a[0].size();
        vector<tuple<int,int,int>>v;
        v.reserve(n*m+2);
        for(int i = 0;i < n;i++) {
            for(int j = 0;j < m;j++) {
                v.emplace_back(a[i][j],i,j);
            }
        }
        sort(v.rbegin(),v.rend());
        long long ans = -inf;
        for(int i = 0;i < n;i++) {
            for(int j = 0;j < m;j++) {
                long long A = a[i][j];
                for(auto& [val, x, y] : v) {
                    if(x == i || y == j) continue;
                    long long cur;
                    for(auto& [val2, x2, y2] : v) {
                        if(x2 == x || y2 == y|| x2 == i || y2 == j) continue;
                        cur=A + val + val2;
                        break;
                    }
                    if(cur > ans) ans = cur;
                    else break;
                }
            }
        }
        return ans;
    }
};
