class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& nums) {
        int n = nums.size();
        int m = nums[0].size();
        long long ans = LLONG_MIN;
        vector<vector<pair<int, int>>> top3InRows(n, vector<pair<int, int>>(3, {INT_MIN, -1}));

        for (int i = 0; i < n; ++i) {
            vector<pair<int, int>> row;
            for (int j = 0; j < m; ++j) {
                row.push_back({nums[i][j], j});
            }
            sort(row.rbegin(), row.rend());
            for (int k = 0; k < 3 && k < m; ++k) {
                top3InRows[i][k] = row[k];
            }
        }
        for (int r1 = 0; r1 < n; ++r1) {
            for (int r2 = r1 + 1; r2 < n; ++r2) {
                for (int r3 = r2 + 1; r3 < n; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (top3InRows[r1][i].second == top3InRows[r2][j].second) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (top3InRows[r1][i].second == top3InRows[r3][k].second || top3InRows[r2][j].second == top3InRows[r3][k].second) continue;
                                long long currentSum = (long long)top3InRows[r1][i].first + top3InRows[r2][j].first + top3InRows[r3][k].first;
                                ans = max(ans, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
