class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long maxSum = LLONG_MIN;
        vector<vector<pair<long long, long long>>> topValues(m, vector<pair<long long,long long>>(3, {-1, -1}));

        for (int i = 0; i < m; ++i) {
            vector<pair<long long, long long>> values;
            for (int j = 0; j < n; ++j) {
                values.emplace_back(board[i][j], j);
            }
            sort(values.rbegin(), values.rend());

            for (int k = 0; k < 3 && k < n; ++k) {
                topValues[i][k] = values[k];
            }
        }
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (topValues[r2][j].second == topValues[r1][i].second) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (topValues[r3][k].second == topValues[r1][i].second || topValues[r3][k].second == topValues[r2][j].second) continue;

                                long long currentSum = topValues[r1][i].first + topValues[r2][j].first + topValues[r3][k].first;
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
