class Solution {
public:
    long long func(int n,int m,vector<vector<pair<int,pair<int,int>>>> &v,vector<int>&row,vector<int>&col,int cnt,int minrow){
        if(cnt==0){
            return 0;
        }
        long long ans=-1e10;
        for(int i=minrow;i<n;i++){
            for(int j=0;j<m;j++){
                if(row[v[i][j].second.first]!=1 && col[v[i][j].second.second]!=1){
                    long long val=v[i][j].first;
                    row[v[i][j].second.first]=1;
                    col[v[i][j].second.second]=1;
                    val+=func(n,m,v,row,col,cnt-1,i+1);
                    row[v[i][j].second.first]=0;
                    col[v[i][j].second.second]=0;
                    ans=max(ans,val);
                }
            }
        }
        return ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=-1e10;
        int n=board.size();
        int dm=board[0].size();
        int m=min(dm,3);
        vector<vector<pair<int,pair<int,int>>>> v(n,vector<pair<int,pair<int,int>>>(m));
        for(int i=0;i<n;i++){
            vector<pair<int,pair<int,int>>> dum;
            for(int j=0;j<dm;j++){
                dum.push_back({board[i][j],{i,j}});
            }
            sort(dum.rbegin(),dum.rend());
            for(int j=0;j<m;j++){
                v[i][j]=dum[j];
            }
        }
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<m;j++){
        //         cout<<v[i][j].first<<" ";
        //     }
        //     cout<<endl;
        // }
        vector<int> row(101,0);
        vector<int> col(101,0);
        return func(n,m,v,row,col,3,0);
        return ans;
    }
};
