#define F first
#define S second
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<pair<int,pair<int,int>>> arr;
        int n = board.size();
        int m = board[0].size();
        for (int i=0;i<n;i++){
            for (int j=0;j<m;j++){
                arr.push_back({board[i][j], {i,j}});
            }
        }
        sort(arr.rbegin(),arr.rend());
        int len = min(2*(n+m) + 3,n*m);
        long long ans = -1e18;
        for (int i=0;i<len;i++){
            for (int j=0;j<len;j++){
                for (int k=0;k<len;k++){
                    int a1 = arr[i].S.F;
                    int b1 = arr[i].S.S;
                    int a2 = arr[j].S.F;
                    int b2 = arr[j].S.S;
                    int a3 = arr[k].S.F;
                    int b3 = arr[k].S.S;
                    if ((a1==a2 || a2==a3 || a1==a3) || (b1==b2 || b2==b3 || b1==b3))continue;
                    ans = max(ans, 1LL*arr[i].F + arr[j].F + arr[k].F);
                }
            }
        }
        return ans;
    }
};
