class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        # 不在同一行/不在同一列
        # 0<i1<m, 0<j1<n
        # 0<i2<m 并且 i2!=i1, 0<j2<n 并且j2!=j1
        m = len(board)
        n = len(board[0])
        row = []
        for i in range(m):
            row.append(sorted(list(range(n)), key = lambda x: board[i][x], reverse = True)[:3]) # 前三大
        res = -inf
        # 枚举第一个王所在的行和列
        for i in range(m):
            # 那么列一定是在前三大中去选
            for j in row[i]:
                value1 = board[i][j]
                # 第二个王所在的行/列不能和第一个相同
                for i1 in range(i+1, m):
                    for j1 in row[i1]:
                        if j1 == j:
                            continue
                        value2 = board[i1][j1]                        
                        for i2 in range(i1+1, m):
                            for j2 in row[i2]:
                                if j2 == j or j2 == j1:
                                    continue
                                value3 = board[i2][j2]
                                res = max(res, value1+value2+value3)
        return res
