def slt_in_grid(a, lx, ly):
    if len(lx) == len(ly) == 1:
        return a[lx[0]][ly[0]]
    else:
        ret = -9999999999
        for j in range(len(ly)):
            ret = max(ret, a[lx[0]][ly[j]] + slt_in_grid(a, lx[1:], ly[:j] + ly[(j + 1):]))
        return ret


class Solution:
    def maximumValueSum(self, a: List[List[int]]) -> int:
        n = len(a)
        m = len(a[0])

        ans = -9999999999
        lv1 = None
        for i in range(n):
            for j in range(m):
                val = slt_in_grid(a, [i], [j])
                if val > ans:
                    ans = val
                    lv1 = (i, j)

        ans = -9999999999
        lx = [lv1[0]]
        ly = [lv1[1]]
        lv2 = None
        for i in range(n):
            for j in range(m):
                if i in lx or j in ly:
                    continue
                val = slt_in_grid(a, lx + [i], ly + [j])
                if val > ans:
                    ans = val
                    lv2 = (i, j)

        ans = -9999999999
        lx.append(lv2[0])
        ly.append(lv2[1])
        lv3 = None
        for i in range(n):
            for j in range(m):
                if i in lx or j in ly:
                    continue
                val = slt_in_grid(a, lx + [i], ly + [j])
                if val > ans:
                    ans = val
                    lv3 = (i, j)

        return ans
