class Solution {
public:
    long long maximumValueSum(vector<vector<int>>&s) 
    {
        long long i,j,l,r,n=s.size(),m=s[0].size(),ans=-2e15;
        vector<vector<long long>>a(n+2,vector<long long>(m+2,-2e15));
        vector<vector<long long>>b(n+2,vector<long long>(m+2,-2e15));
        vector<vector<long long>>c(n+2,vector<long long>(m+2,-2e15));
        vector<vector<long long>>d(n+2,vector<long long>(m+2,-2e15));
        vector<vector<long long>>L(n+2,vector<long long>(m+2,-2e15));
        vector<vector<long long>>R(n+2,vector<long long>(m+2,-2e15));
        for(i=1;i<=n;i++)
        {
            for(j=1;j<=m;j++) a[i][j]=b[i][j]=c[i][j]=d[i][j]=L[i][j]=R[i][j]=s[i-1][j-1];
        }
        for(i=1;i<=n;i++)
        {
            for(j=1;j<=m;j++) a[i][j]=max({a[i-1][j],a[i][j-1],a[i][j]}),L[i][j]=max(L[i][j],L[i][j-1]);
        }
        for(i=1;i<=n;i++)
        {
            for(j=m;j>0;j--)  b[i][j]=max({b[i-1][j],b[i][j+1],b[i][j]}),R[i][j]=max(R[i][j],R[i][j+1]);
        }
        for(i=n;i>0;i--)
        {
            for(j=1;j<=m;j++) c[i][j]=max({c[i+1][j],c[i][j-1],c[i][j]});
        }
        for(i=n;i>0;i--)
        {
            for(j=m;j>0;j--)  d[i][j]=max({d[i+1][j],d[i][j+1],d[i][j]});
        }
        for(i=1;i<=n;i++)
        {
            for(j=1;j<=m;j++)
            {
                for(l=1;l<=n;l++)
                {
                    if(l==i) continue;
                    long long x=i,y=l,mx=-2e15;
                    if(x>y) swap(x,y);
                    mx=max(mx,b[x-1][j+1]);
                    mx=max(mx,d[y+1][j+1]);
                    for(r=x+1;r<y;r++)
                    {
                        mx=max(mx,R[r][j+1]);
                    }
                    ans=max(ans,s[i-1][j-1]+L[l][j-1]+mx);
                    mx=-2e15;
                    mx=max(mx,a[x-1][j-1]);
                    mx=max(mx,c[y+1][j-1]);
                    for(r=x+1;r<y;r++)
                    {
                        mx=max(mx,L[r][j-1]);
                    }
                    ans=max(ans,s[i-1][j-1]+R[l][j+1]+mx);
                } 
            }
        }
        return ans;
    }
};
