class Solution {
    public long maximumValueSum(int[][] b) {

        int r = b.length;
        int c = b[0].length;
        long ms = Long.MIN_VALUE;
        long[][] bv = new long[r][3];
        int[][] bc = new int[r][3];


        for (int i = 0; i < r; ++i) {
            List<Pair<Long, Integer>> v = new ArrayList<>();
            for (int j = 0; j < c; ++j) {
                v.add(new Pair<>((long) b[i][j], j));
            }
            v.sort((a, b1) -> b1.getKey().compareTo(a.getKey()));

            for (int k = 0; k < 3 && k < c; ++k) {
                bv[i][k] = v.get(k).getKey();
                bc[i][k] = v.get(k).getValue();
            }
        }

        for (int i = 0; i < r; ++i) {
            for (int j = i + 1; j < r; ++j) {
                for (int k = j + 1; k < r; ++k) {
                    for (int l = 0; l < 3; ++l) {
                        for (int m = 0; m < 3; ++m) {
                            if (bc[j][m] == bc[i][l]) continue;
                            for (int n = 0; n < 3; ++n) {
                                if (bc[k][n] == bc[i][l] || bc[k][n] == bc[j][m]) continue;

                                long cs = bv[i][l] + bv[j][m] + bv[k][n];
                                ms = Math.max(ms, cs);
                            }
                        }
                    }
                }
            }
        }

        return ms;
    }

    private static class Pair<K, V> {
        private final K key;
        private final V value;

        public Pair(K key, V value) {
            this.key = key;
            this.value = value;
        }

        public K getKey() {
            return key;
        }

        public V getValue() {
            return value;
        }
    }
}
