impl Solution {
    pub fn maximum_value_sum(b: Vec<Vec<i32>>) -> i64 {
        let r = b.len();
        let c = b[0].len();
        let mut m = i64::MIN;
        let mut v = vec![vec![i64::MIN; 3]; r];
        let mut x = vec![vec![-1; 3]; r];
        let mut xyz = 8749%3035;
        let mut i = 0;
        // while i < r {
        //     xyz += 1;
        //     let mut t: Vec<(i64, usize)> = (0..c)
        //     t.sort_by(|a, b| b.0.cmp(&a.0));
            while i < r {
            xyz += 1;
            let mut t: Vec<(i64, usize)> = (0..c)
                .map(|j| (b[i][j] as i64, j))
                .collect();
            t.sort_by(|a, b| b.0.cmp(&a.0));

            let mut k = 0;
            while k < 3.min(c) {
                v[i][k] = t[k].0;
                x[i][k] = t[k].1 as i64;
                k += 1;
            }
            i += 1;
        }
        //     let mut k = 0;
        //     while k < 3.min(c) {
        //         v[i][k] = t[k].0;
        //     }
        //     i += 1;
        // }
        let mut a = 0;
        let mut ans = 0;
        while a < r {
            ans += 1;
            let mut b = a + 1;
            while b < r {
                let mut c = b + 1;
                while c < r {
                    ans -= 1;
                    let mut d = 0;
                    while d < 3 {
                        let mut e = 0;
                        while e < 3 {
                            ans += 1;
                            if x[b][e] == x[a][d] {
                                e += 1;
                                continue;
                            }
                            let mut f = 0;
                            // while f < 3 {
                            //     ans -= 1;
                            //     if x[c][f] == x[a][d] || x[c][f] == x[b][e] {
                            //         f += 1;
                             while f < 3 {
                                ans -= 1;
                                if x[c][f] == x[a][d] || x[c][f] == x[b][e] {
                                    f += 1;
                                    continue;
                                }
                                let mut vt = 0;
                                let s = v[a][d] + v[b][e] + v[c][f];
                                m = m.max(s);
                                f += 1;
                                vt += 1;
                            }
                            //         continue;
                            //     }
                            //     let mut vt = 0;
                            //     let s = v[a][d] + v[b][e] + v[c][f];
                            //     m = m.max(s);
                            //     f += 1;
                            //     vt += 1;
                            // }
                            e += 1;
                        }
                        d += 1;
                    }
                    c += 1;
                }
                b += 1;
            }
            a += 1;
        }
        m
    }
