class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& arr) {
        long long rows = arr.size();
        long long cols = arr[0].size();
        vector<vector<pair<long long, long long>>> v1(cols);
        long long ans = LLONG_MIN;

        for (long long col = 0; col < cols; col++) {
            vector<pair<long long, long long>> v2;
            for (long long row = 0; row < rows; row++) {
                v2.push_back({arr[row][col], row});
            }
            sort(v2.begin(), v2.end());
            reverse(v2.begin(),v2.end());
            for (long long k = 0; k < min(3LL, rows); k++) {
                v1[col].push_back(v2[k]);
            }
        }

        for (long long i = 0; i < cols; i++) {
            for (long long j = i + 1; j < cols; j++) {
                for (long long k = j + 1; k < cols; k++) {
                    for (auto& val1 : v1[i]) {
                        for (auto& val2 : v1[j]) {
                            for (auto& val3 : v1[k]) {
                                if (val1.second != val2.second && val1.second != val3.second && val2.second != val3.second) {
                                    long long curr = val1.first + val2.first + val3.first;
                                    ans = max(ans, curr);
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
