    bool static op(pair<int, int>& a, pair<int, int>& b){
        return a.first > b.first;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans = LLONG_MIN;  
        int n = board[0].size();
        int m = board.size();

        vector<vector<pair<int, int>>> vis(m);
        for(int i=0; i<m; i++){
            vector<pair<int, int>> tmp, tmp2;
            for(int j=0; j<n; j++){
                tmp.push_back({board[i][j], j});
            }
            sort(tmp.begin(), tmp.end(), op);
            for(int k=0; k<3; k++){
                tmp2.push_back(tmp[k]);
            }
            vis[i] = tmp2;
        }

        for(int i=0; i<m; i++){
            for(int j=0; j<vis[i].size(); j++){
                int p1 = i, p2 = vis[i][j].second;
                for(int k=i+1; k<m; k++){
                    for(int l=0; l<vis[k].size(); l++){
                        int q1 = k, q2 = vis[k][l].second;
                        if(p2!=q2){  
                            for(int x=k+1; x<m; x++){
                                for(int y=0; y<vis[x].size(); y++){
                                    int r1 = x, r2 = vis[x][y].second;
                                    if(r2 != p2 && r2 != q2){  
                                        long long sum = (long long)board[p1][p2] + (long long)board[q1][q2] + (long long)board[r1][r2];
                                        ans = max(ans, sum);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
