long long dp[101][102][102];
class Solution {
    long long lMin=-1e10;
    long long helper(int i,int b1,int b2,int n ,int m,vector<vector<int>>& board){
        if(b2<b1)return helper(i,b2,b1,n,m,board);
        if(i==n)return lMin;
        if(dp[i][b1][b2]!=lMin)return dp[i][b1][b2];
        long long ans=helper(i+1,b1,b2,n,m,board);
        for(int j=0;j<m;++j){
            if(j==b1||j==b2)continue;
            if(b1!=101&&b2!=101)ans=max(ans,(long long)board[i][j]);
            else {long long c=helper(i+1,b1,j,n,m,board);
            if(c!=lMin){c+=board[i][j];
            ans=max(ans,c);}}
        }return dp[i][b1][b2]=ans;
    }
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(),m=board[0].size();
        for(int i=0;i<101;++i){
            for(int j=0;j<102;++j){
            for(int k=0;k<102;++k){
            dp[i][j][k]=lMin;
        }
        }
        }
        return helper(0,101,101,n,m,board);
    }
};
