class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long maxSum = LLONG_MIN;

        vector<vector<pair<int, int>>> max3(m);

        for (int i = 0; i < m; ++i) {
            vector<pair<int, int>> row;
            for (int j = 0; j < n; ++j) {
                row.push_back({board[i][j], j});
            }
            sort(row.rbegin(), row.rend());

            for (int k = 0; k < min(3, (int)row.size()); ++k) {
                max3[i].push_back(row[k]);
            }
        }

        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (auto [val1, c1] : max3[r1]) {
                        for (auto [val2, c2] : max3[r2]) {
                            if (c1 == c2) continue;  
                            for (auto [val3, c3] : max3[r3]) {
                                if (c3 == c1 || c3 == c2) continue;  
                                long long sum = (long long)val1 + val2 + val3;
                                maxSum = max(maxSum, sum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
