class Solution {
public:
    using ll = long long;

    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        ll res = -1e18;
        vector<vector<int> > id(n);

        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                id[i].push_back(j);
            }
            sort(id[i].begin(), id[i].end(), [&] (int p, int q){
                return a[i][p] > a[i][q];
            });
        }
        vector<ll> val(m + 2, -1e18);
        for(int i = 0; i < n; i++) {
            if(i >= 2) {
                for(int j = 0; j < m; j++) {
                    res = max(res, val[j] + a[i][j]);
                }
            }
            set<array<ll, 3> > st;
            for(int i1 = i - 1; i1 >= 0; i1--) {
                for(int j = 0; j < min(7, m); j++) {
                    for(int j1 = 0; j1 < min(7, m); j1++) {
                        int J = id[i][j], J1 = id[i1][j1];
                        if(J != J1) {
                            st.insert({J, J1, a[i][J] + a[i1][J1]});
                        }
                    }
                }
            }
            for(auto [j1, j2, x]: st) {
                for(int j = 0; j < m; j++) {
                    if(j1 == j or j2 == j) continue;
                    val[j] = max(val[j], x);
                }
            }

        }
        return res;
    }
};
