class Solution
{
public:
    vector<vp> dp1;
    lli ans = -1e18;
    long long maximumValueSum(vector<vector<int>> &a)
    {
        fastio;
        int kk = sz(a), m = sz(a[0]);
        dp1.resize(kk, vp(3, {INT_MIN, -1}));

        for (int i = 0; i < kk; ++i)
        {
            vp temp;
            for (int j = 0; j < m; ++j)
                temp.push_back({a[i][j], j});
            sort(rall(temp));
            for (int k = 0; k < min(3, m); ++k)
                dp1[i][k] = temp[k];
        }

        for (int i = 0; i < kk; ++i)
        {
            for (int j = i + 1; j < kk; ++j)
            {
                for (int k = j + 1; k < kk; ++k)
                {
                }
            }
        }
        loop(i, 0, kk) loop(j, i + 1, kk) loop(k, j + 1, kk) upd(i, j, k);

        return ans;
    }

    void upd(int i, int j, int k)
    {
        fastio;
        for (int r1 = 0; r1 < 3; ++r1)
        {
            for (int r2 = 0; r2 < 3; ++r2)
            {
                if (dp1[i][r1].second != dp1[j][r2].second)
                {
                    for (int i3 = 0; i3 < 3; ++i3)
                    {
                        if (dp1[i][r1].second == dp1[k][i3].second ||
                            dp1[j][r2].second == dp1[k][i3].second)
                            continue;
                        ans = max(ans, (long long)dp1[i][r1].first + dp1[j][r2].first + dp1[k][i3].first);
                    }
                }
            }
        }
    }
};
