class Solution {
public:
    typedef long long ll;
    ll solve2(vector<vector<vector<int>>>&b,int in,int c,unordered_set<int>vis)
    {
        if(in>=b.size() && c==2)
        {
            return -4*ll(pow(10,9));
        }
        if(in>=b.size()-1 && c==1)
        {
            return -4*ll(pow(10,9));
        }
        if(c==0)
        {
            ll a{-4*ll(pow(10,9))};
            for(int i{};i<3;i++)
            {
                vis.insert(b[in][i][1]);
                a=max(a,b[in][i][0]+solve2(b,in+1,c+1,vis));
                vis.erase(b[in][i][1]);
            }
            return a;
        }
        else if(c==1)
        {
                ll a1=solve2(b,in+1,c,vis);
                ll a2{-4*ll(pow(10,9))};
                ll ans{a1};
                for(int i{};i<3;i++)
                {
                    if(vis.count(b[in][i][1])==0)
                    {
                        vis.insert(b[in][i][1]);
                        a2=b[in][i][0]+solve2(b,in+1,c+1,vis);
                        ans=max(a2,ans);
                        vis.erase(b[in][i][1]);  
                    }
                }
                return ans;  
        }
        else if(c==2)
        {

                ll a1=solve2(b,in+1,c,vis);
                ll a2{-4*ll(pow(10,9))};
                ll ans{a1};
                for(int i{};i<3;i++)
                {
                    if(vis.count(b[in][i][1])==0)
                    {
                        a2=b[in][i][0];
                        ans=max(ans,a2);
                    }
                }
                return ans;
        }
        else
        {
            return -4*ll(pow(10,9));
        }
    }
    vector<vector<int>> solve1(vector<int>&x)
    {
        vector<vector<int>>s;

        for(int i{};i<x.size();i++)
        {
            s.push_back({x[i],i});
        }
        sort(s.begin(),s.end(),greater<vector<int>>());
        return {s[0],s[1],s[2]};
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<vector<int>>>b(board.size());
        for(int i{};i<board.size();i++)
        {
            b[i]=solve1(board[i]);
        }
        sort(b.begin(),b.end(),greater<vector<vector<int>>>());
        // for(int i{};i<board.size();i++)
        // {
        //     cout<<b[i][0][0]<<" "<<b[i][1][0]<<" "<<b[i][2][0]<<endl;
        // }
        // cout<<endl;
        return solve2(b,0,0,unordered_set<int>());
    }
};
