class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        lli n= board.size();
        lli m = board[0].size();
        vector<vector<pair<lli,lli>>> topNum(n);
        for(int i=0;i<n;i++)
        {
            vector<pair<int,int>> temp;
            for(int j=0;j<board[i].size();j++)
            {
                temp.push_back({board[i][j],j});
            }
            sort(all(temp));
            set<int>temp2;
            for(int j=temp.size()-1;j>=0;j--)
            {
                temp2.insert(temp[j].first);
                if(temp2.size()<=3)
                {
                    topNum[i].push_back(temp[j]);
                }
            }
        }
        lli ans = smol;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(i==j)continue;
                for(int k=0;k<n;k++)
                {
                    if(i==k || j==k)continue;
                    int s1 = topNum[i].size();
                    int s2 = topNum[j].size();
                    int s3 = topNum[k].size();
                    for(int p=0;p<s1;p++)
                    {
                        // auto it =topNum[p];
                        // cout<<it.first<<"\n";
                        for(int q =0;q<s2;q++)
                        {
                            if((topNum[i][p].second)==topNum[j][q].second)continue;
                            for(int r=0;r<s3;r++)
                            {
                                if(topNum[i][p].second==topNum[k][r].second || topNum[j][q].second==topNum[k][r].second)continue;
                                ans = max(ans, topNum[i][p].first+topNum[j][q].first+topNum[k][r].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
