class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {

        int n = board.size();
        int m = board[0].size();

        vector<vector<pair<long long int, long long int>>> row(n, vector<pair<long long int, long long int>> (3, {-1e9, n}));

        for(int i=0; i<n; i++)
        {

            vector<pair<long long int, long long int>> V;
            for(int j=0; j<3; j++)
            {
                V.push_back({board[i][j], j});
            }
            sort(V.begin(), V.end());

            for(int j = 3; j<m; j++)
            {
                if(board[i][j]>V[0].first)
                {
                    V[0] = {board[i][j], j};
                    sort(V.begin(), V.end());
                }
            }

            row[i] = V;

        }

        // for(int i=0; i<n; i++)
        // {
        //     for(int j=0; j<3; j++)
        //     {
        //         cout<<row[i][j].first<<"/"<<row[i][j].second<<"  ";
        //     }
        //     cout<<" ^\n";
        // }

        long long mxv = -3e9;

        for(int i=0; i<n-2; i++)
        {
            for(int j=i+1; j<n-1; j++)
            {
                for(int k = j+1; k<n; k++)
                {

                    for(int p=2; p>=0; p--)
                    {
                        for(int q=2; q>=0; q--)
                        {

                            if(row[i][p].second==row[j][q].second)
                            {
                                continue;
                            }
                            else
                            {
                                if((row[i][p].second!=row[k][2].second)&&(row[j][q].second!=row[k][2].second))
                                {
                                    long long int x1 = row[i][p].first+row[j][q].first+row[k][2].first;
                                    mxv = max(x1, mxv);
                                }
                                else if((row[i][p].second!=row[k][1].second)&&(row[j][q].second!=row[k][1].second))
                                {
                                    long long int x1 = row[i][p].first+row[j][q].first+row[k][1].first;
                                    mxv = max(x1, mxv);
                                }
                                else if((row[i][p].second!=row[k][0].second)&&(row[j][q].second!=row[k][0].second))
                                {
                                    long long int x1 = row[i][p].first+row[j][q].first+row[k][0].first;
                                    mxv = max(x1, mxv);
                                }

                            }

                        }
                    }

                }
            }
        }

        return mxv;

    }
};
