class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=-1e10;
        int n=board.size();
        int m=board[0].size();
        vector<vector<pair<int,int>>>v(n);
        for(int row1=0;row1<n;row1++){
            vector<pair<int,int>>temp;
            for(int row2=0;row2<m;row2++){
                temp.push_back({board[row1][row2],row2});
            }
            sort(temp.begin(),temp.end());
            vector<pair<int,int>>p;
            for(int x=m-1;x>=m-3;x--){
                p.push_back(temp[x]);
            }
            v[row1]=p;
        }
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<3;j++){
        //         cout<<v[i][j].first<<","<<v[i][j].second<<"  ";
        //     }
        //     cout<<endl;
        // }
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                for(int k=0;k<n;k++){
                    for(int a=0;a<3;a++){
                        for(int b=0;b<3;b++){
                            for(int c=0;c<3;c++){
                                if(v[i][a].second!=v[j][b].second && v[j][b].second!=v[k][c].second && v[i][a].second!=v[k][c].second && i!=j && j!=k && k!=i){
                                    long long temp_ans=1LL*v[i][a].first+1LL*v[j][b].first+1LL*v[k][c].first;
                                    ans=max(ans,temp_ans);
                                }
                            }
                        }
                    }
                }

            }

        }
// 3,2  2,1  1,0  
// 6,2  5,1  4,0  
// 9,2  8,1  7,0 

        return ans;
    }
};
