class Solution {
public:
    long long maximumValueSum(vector<vector<int >>& board) {
       long long maxSum = -1e18;
long long n = board.size();
long long m = board[0].size();

vector<vector<pair<long long, long long>>> st(n, vector<pair<long long, long long>>(3, {0, -1}));

for (long long i = 0; i < n; i++) {
    vector<pair<long long, long long>> temp;  
    for (long long j = 0; j < m; j++) {
        temp.push_back({board[i][j], j});
    }
    sort(temp.begin(), temp.end());  
    reverse(temp.begin(), temp.end());
    int k = 0;
    while (k < 3) {
        st[i][k] = temp[k];
        k++;
    }
}

for (long long i = 0; i < n; i++) {
    for (long long j = i + 1; j < n; j++) {
        for (long long k = j + 1; k < n; k++) {
            for (long long a = 0; a < 3; a++) {
                for (long long b = 0; b < 3; b++) {
                    for (long long c = 0; c < 3; c++) {
                        long long col1 = st[i][a].second;
                        long long col2 = st[j][b].second;
                        long long col3 = st[k][c].second;
                        if (col1 != col2 && col1 != col3 && col2 != col3) {
                            long long sum = st[i][a].first + st[j][b].first + st[k][c].first;
                            maxSum = max(maxSum, sum);
                        }
                    }
                }
            }
        }
    }
}

return maxSum;

    }
};
