#define ll long long
class Solution {
public:
    ll maximumValueSum(vector<vector<int>>& nums) {
        int n = nums.size();
        int m = nums[0].size();

        // Store the top 3 maximum values for each row and their corresponding columns
        vector<vector<pair<ll, ll>>> row_max(n, vector<pair<ll, ll>>(3, {-1, -1}));

        for (int i = 0; i < n; i++) {
            // Use a temporary vector to sort and pick the top 3 values
            vector<pair<int, int>> temp;
            for (int j = 0; j < m; j++) {
                temp.push_back({nums[i][j], j});
            }
            // Sort by values in descending order
            sort(temp.rbegin(), temp.rend());
            for (int k = 0; k < 3 && k < m; k++) {
                row_max[i][k] = temp[k];
            }
        }

        ll maxi = -1e18;

        // Iterate over all combinations of three different rows
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                for (int k = j + 1; k < n; k++) {
                    // Check all combinations of the top 3 columns for these rows
                    for (int a = 0; a < 3; a++) {
                        for (int b = 0; b < 3; b++) {
                            if (row_max[i][a].second == -1 || row_max[j][b].second == -1) continue;
                            if (row_max[i][a].second == row_max[j][b].second) continue;

                            for (int c = 0; c < 3; c++) {
                                if (row_max[k][c].second == -1) continue;
                                if (row_max[k][c].second == row_max[i][a].second || 
                                    row_max[k][c].second == row_max[j][b].second) continue;

                                ll current_sum = row_max[i][a].first + row_max[j][b].first + row_max[k][c].first;
                                maxi = max(maxi, current_sum);
                            }
                        }
                    }
                }
            }
        }

        return maxi;
    }
};
