class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        cells = []
        for i, line in enumerate(board):
            for j, e in enumerate(line):
                bisect.insort(cells, (e, i, j))
        cells.reverse()
        if len(cells) > 18:
            cells = cells[:18]

        res = float("-inf")
        for i in range(len(cells)):
            v1, x1, y1 = cells[i]
            for j in range(len(cells)):
                v2, x2, y2 = cells[j]
                if x1 == x2 or y1 == y2:
                    continue
                for k in range(len(cells)):
                    v3, x3, y3 = cells[k]
                    if x1 == x3 or x2 == x3 or y1 == y3 or y2 == y3:
                        continue
                    res = max(res, v1 + v2 + v3)
        return res
