typedef long long LL;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        LL ans = 0;
        int n = board.size(), m = board[0].size();
        vector<vector<pair<int, int>>> arr(m);
        for(int i = 0; i < m; i++){
            for(int j = 0; j < n; j++){
                arr[i].push_back({board[j][i], j});
            }
            sort(arr[i].begin(), arr[i].end(), [&](pair<int, int>& a, pair<int, int>& b){
                return a.first > b.first;
            });
        }
        LL res = -3e9;
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                LL ans = board[i][j], mx = -3e9;
                for(int k = 0; k < m; k++){
                    if(j==k) continue;
                    int idx1 = -1, idx2 = -1;
                    for(int tmp = 0; tmp < 3; tmp++){
                        if(arr[k][tmp].second!=i){
                            if(idx1==-1) idx1 = tmp;
                            else if(idx2==-1) idx2 = tmp;
                        }
                    }
                    auto c = arr[k][idx1], d = arr[k][idx2];
                    for(int l = 0; l < k; l++){
                        if(l==j) continue;
                        idx1 = -1, idx2 = -1;
                        for(int tmp = 0; tmp < 3; tmp++){
                            if(arr[l][tmp].second!=i){
                                if(idx1==-1) idx1 = tmp;
                                else if(idx2==-1) idx2 = tmp;
                            }
                        }
                        auto a = arr[l][idx1], b = arr[l][idx2];
                        // if(j==1) cout << a.second << ' ' << c.second << endl;
                        if(a.second!=c.second) mx = max(mx, 1ll*a.first+c.first);
                        else {
                            mx = max(mx, 1ll*a.first+d.first);
                            mx = max(mx, 1ll*c.first+b.first);
                        }
                    }
                } 
                res = max(res, ans+mx);
                // cout << mx+ans << ' ';
            }
            // cout << endl;
        }
        return res;
    }
};
