class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {



                 int m = board.size();
        int n = board[0].size();
        long long maxi = LLONG_MIN;
        vector<vector<long long>> bestV(m, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> bestC(m, vector<int>(3, -1));

        for (int i = 0; i < m; ++i) {
            vector<pair<long long, int>> col;
            for (int j = 0; j < n; ++j) {
                col.emplace_back(board[i][j], j);
            }
            sort(col.rbegin(), col.rend()); 

            for (int k = 0; k < 3 && k < n; ++k) {
                bestV[i][k] = col[k].first;
                bestC[i][k] = col[k].second;
            }
        }


        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (bestC[r2][j] == bestC[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (bestC[r3][k] == bestC[r1][i] || bestC[r3][k] == bestC[r2][j]) continue;

                                long long currentSum = bestV[r1][i] + bestV[r2][j] + bestV[r3][k];
                                maxi = max(maxi, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxi;
    }
};
