class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<pair<long long, long long > > > v(board[0].size(), vector<pair<long long, long long > > (3));

        for(int j = 0; j < board[0].size(); j++)
        {
            priority_queue<pair<int, int > > pq;
            for(int i = 0; i < board.size(); i++)
            {
                pq.push({board[i][j], i});
            }
            v[j][0] = pq.top();
            pq.pop();
            v[j][1] = pq.top();
            pq.pop();
            v[j][2] = pq.top();
        } 

        long long maxi = -1e15;

        for(int i = 0; i < board[0].size(); i++)
        {
            for(int j = i + 1; j < board[0].size(); j++)
            {
                for(int k = j + 1; k < board[0].size(); k++)
                {
                    for(int row1 = 0; row1 < 3; row1++)
                    {
                        for(int row2 = 0; row2 < 3; row2++)
                        {
                            for(int row3 = 0; row3 < 3; row3++)
                            {
                                if(v[i][row1].second == v[j][row2].second || v[i][row1].second == v[k][row3].second || v[j][row2].second == v[k][row3].second)
                                {
                                    continue;
                                }
                                // cout << i << " " << j << " " << k << " ijk " << endl;
                                // cout << v[i][row1].second << " " << v[j][row2].second << " " << v[k][row3].second << endl;
                                // cout << v[i][row1].first << " " << v[j][row2].first << " " << v[k][row3].first << endl << endl;;
                                maxi = max(maxi, v[i][row1].first + v[j][row2].first + v[k][row3].first);
                            }
                        }
                    }
                }
            }
        }
        return maxi;
    }
};
