class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:

        imp = []

        m, n = len(board), len(board[0])

        for i in range(m):
            queue = []
            for j in range(n):
                heappush(queue, (board[i][j], j))
                if len(queue) > 3:
                    heappop(queue)
            queue.sort()
            imp.append(queue)

        def maxCost(i, j, k):
            maxi = float("-inf")
            for (v1, c1), (v2, c2), (v3, c3) in product(imp[i], imp[j], imp[k]):
                if c1 != c2 and c1 != c3 and c2 != c3:
                    curr = v1 + v2 + v3
                    maxi = max(maxi, curr)
            return maxi


        maximum = float("-inf")
        for i in range(m-2):
            for j in range(i+1, m-1):
                for k in range(j+1, m):
                   maximum = max(maximum, maxCost(i, j, k)) 

        return maximum
