class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        pqs = []
        for row in board:
            pq = [(-inf, -1)]*3
            for ix, n in enumerate(row):
                heappushpop(pq, (n, ix))
            pqs.append(pq)

        top9 = [(-inf, -1, -1)]*30
        for kx in range(len(pqs)):
            for n, ix in pqs[kx]:
                heappushpop(top9, (n, ix, kx))

        ans = -inf
        for n1, ix1, jx1 in top9:
            for n2, ix2, jx2 in top9:
                if ix2 == ix1 or jx2 == jx1:
                    continue
                for n3, ix3, jx3 in top9:
                    if ix3 in [ix1, ix2] or jx3 in [jx2, jx1]:
                        continue

                    ans = max(ans, n1+n2+n3)


        return ans
