import java.util.*;

class Solution {
    public long maximumValueSum(int[][] board) {
        int rowCount = board.length;
        int colCount = board[0].length;
        long maxSum = Long.MIN_VALUE;
        long[][] topValues = new long[rowCount][3];
        int[][] topColumns = new int[rowCount][3];


        for (int row = 0; row < rowCount; row++) {
            List<Cell> valueColumnPairs = new ArrayList<>();
            for (int col = 0; col < colCount; col++) {
                valueColumnPairs.add(new Cell(board[row][col], col));
            }
            valueColumnPairs.sort((a, b) -> Long.compare(b.value, a.value)); 

            for (int rank = 0; rank < 3 && rank < colCount; rank++) {
                topValues[row][rank] = valueColumnPairs.get(rank).value;
                topColumns[row][rank] = valueColumnPairs.get(rank).column;
            }
        }


        for (int row1 = 0; row1 < rowCount; row1++) {
            for (int row2 = row1 + 1; row2 < rowCount; row2++) {
                for (int row3 = row2 + 1; row3 < rowCount; row3++) {

                    for (int col1 = 0; col1 < 3; col1++) {
                        for (int col2 = 0; col2 < 3; col2++) {
                            if (topColumns[row2][col2] == topColumns[row1][col1]) continue;
                            for (int col3 = 0; col3 < 3; col3++) {
                                if (topColumns[row3][col3] == topColumns[row1][col1] || topColumns[row3][col3] == topColumns[row2][col2]) continue;

                                long currentSum = topValues[row1][col1] + topValues[row2][col2] + topValues[row3][col3];
                                maxSum = Math.max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }

    private static class Cell {
        long value;
        int column;

        Cell(long value, int column) {
            this.value = value;
            this.column = column;
        }
    }
}
