class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int m = b.size(), n = b[0].size();
        vector<tuple<long long, int>> rTop3[m], cTop3[n];

        for (int i = 0; i < m; ++i) {
            vector<pair<long long, int>> v;
            for (int j = 0; j < n; ++j)
                v.emplace_back(b[i][j], j);
            sort(v.rbegin(), v.rend());
            rTop3[i] = {v[0], v[1], v[2]};
        }

        for (int j = 0; j < n; ++j) {
            vector<pair<long long, int>> v;
            for (int i = 0; i < m; ++i)
                v.emplace_back(b[i][j], i);
            sort(v.rbegin(), v.rend());
            cTop3[j] = {v[0], v[1], v[2]};
        }

        vector<tuple<long long, int, int>> cands;
        for (int i = 0; i < m; ++i)
            for (auto& [v, j] : rTop3[i])
                cands.emplace_back(v, i, j);
        for (int j = 0; j < n; ++j)
            for (auto& [v, i] : cTop3[j])
                cands.emplace_back(v, i, j);

        long long maxSum = LLONG_MIN;
        int len = cands.size();
        for (int i = 0; i < len; ++i)
            for (int j = i + 1; j < len; ++j)
                for (int k = j + 1; k < len; ++k) {
                    auto [v1, r1, c1] = cands[i];
                    auto [v2, r2, c2] = cands[j];
                    auto [v3, r3, c3] = cands[k];
                    if (r1 != r2 && r1 != r3 && r2 != r3 && c1 != c2 && c1 != c3 && c2 != c3)
                        maxSum = max(maxSum, v1 + v2 + v3);
                }

        return maxSum;
    }
};
