from itertools import permutations

class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        Max = float('-inf')
        best_values = [[float('-inf')] * 3 for _ in range(m)]
        best_columns = [[-1] * 3 for _ in range(m)]

        for i in range(m):
            col_values = [(board[i][j], j) for j in range(n)]
            col_values.sort(reverse=True)

            for k in range(min(3, n)):
                best_values[i][k] = col_values[k][0]
                best_columns[i][k] = col_values[k][1]

        for r1 in range(m):
            for r2 in range(r1 + 1, m):
                for r3 in range(r2 + 1, m):
                    for i in range(3):
                        for j in range(3):
                            if best_columns[r2][j] == best_columns[r1][i]:
                                continue
                            for k in range(3):
                                if best_columns[r3][k] == best_columns[r1][i] or best_columns[r3][k] == best_columns[r2][j]:
                                    continue

                                current_sum = best_values[r1][i] + best_values[r2][j] + best_values[r3][k]
                                Max = max(Max, current_sum)

        return Max
