int n, m;
pair<int, int> b[511][511], c[511][511];
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        n = a.size(), m = a[0].size();
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                b[i][j] = {a[i][j], j};
            }
            sort(b[i], b[i]+m);
            reverse(b[i], b[i]+m);
        }
        for (int i = n-1; i >= 0; --i) {
            for (int j = 0; j < m; ++j) {
                if (i != n-1) c[i][j] = {max(c[i+1][j].first, a[i][j]), j};
                else c[i][j] = {a[i][j], j};
            }
        }
        for (int i = 0; i < n; ++i) {
            sort(c[i], c[i]+m);
            reverse(c[i], c[i]+m);
        }
        long long ans = -1e9*3;
        for (int i = 0; i < n; ++i) {
            for (int j = i+1; j < n-1; ++j) {
                for (int x = 0; x < min(3, m); ++x) {
                    for (int y = 0; y < min(3, m); ++y) {
                        if (b[i][x].second == b[j][y].second) continue;
                        for (int k = 0; k < m; ++k) {
                            if (c[j+1][k].second != b[i][x].second && c[j+1][k].second != b[j][y].second) {
                                ans = max(ans, (long long)b[i][x].first+b[j][y].first+c[j+1][k].first);
                                break;
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
