class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        cells = []

        for i in range(m):
            for j in range(n):
                cells.append((board[i][j], i, j))

        cells.sort(reverse=True)

        max_sum = float('-inf')
        K = min(100, len(cells))  

        for i in range(K):
            val1, r1, c1 = cells[i]
            for j in range(i + 1, K):
                val2, r2, c2 = cells[j]
                if r1 == r2 or c1 == c2:
                    continue
                for k in range(j + 1, K):
                    val3, r3, c3 = cells[k]
                    if r3 in {r1, r2} or c3 in {c1, c2}:
                        continue
                    current_sum = val1 + val2 + val3
                    if current_sum > max_sum:
                        max_sum = current_sum

        return max_sum
