import sys
class Solution(object):
    def maximumValueSum(self, board):
        """
        :type board: List[List[int]]
        :rtype: int
        """
        m = len(board)
        n = len(board[0])
        maxSum = -sys.maxsize - 1  
        best_values = [[-sys.maxsize - 1 for _ in range(3)] for _ in range(m)]
        bCol = [[-1 for _ in range(3)] for _ in range(m)]
        for row in range(m):
            column_values = [(board[row][col], col) for col in range(n)]
            column_values.sort(reverse=True) 
            for k in range(min(3, n)):
                best_values[row][k] = column_values[k][0]
                bCol[row][k] = column_values[k][1]
        for r1 in range(m):
            for r2 in range(r1 + 1, m):
                for r3 in range(r2 + 1, m):
                    for i in range(3):
                        for j in range(3):
                            if bCol[r2][j] == bCol[r1][i]:
                                continue
                            for k in range(3):
                                if bCol[r3][k] == bCol[r1][i] or bCol[r3][k] == bCol[r2][j]:
                                    continue

                                current_sum = best_values[r1][i] + best_values[r2][j] + best_values[r3][k]
                                maxSum = max(maxSum, current_sum)

        return maxSum
