class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int m = a.size();
        int n = a[0].size();
        vector<vector<vector<int>>> v(m);

        for(int i = 0; i < m; i++) {
            vector<vector<int>> vv;
            for(int j = 0; j < n; j++) {
                vv.push_back({a[i][j], j});
            }
            sort(vv.begin(), vv.end());
            reverse(vv.begin(), vv.end());
            for(int j = 0; j < 3; j++) {
                v[i].push_back({vv[j][0], vv[j][1]});
            }
        }
        long long ans = -1e15;
        for(int i = 0; i < m; i++) {
            for(int j = i + 1; j < m; j++) {
                for(int k = j + 1; k < m; k++) {
                    long long m = -1e15;
                    for(int ii = 0; ii < 3; ii++) {
                        for(int jj = 0; jj < 3; jj++) {
                            for(int kk = 0; kk < 3; kk++) {
                                if(v[i][ii][1] != v[j][jj][1] && v[i][ii][1] != v[k][kk][1] && v[j][jj][1] != v[k][kk][1]) {
                                    m = max(m, 0ll + v[i][ii][0] + v[j][jj][0] + v[k][kk][0]);
                                }
                            }
                        }
                    }

                    ans = max(ans, m);
                }


            }
        }
        return ans;


    }
};
