class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        vector<pair<int, pair<int, int>>> v;
        for(int i = 0; i < b.size(); i++) {
            for(int j = 0; j < b[i].size(); j++) {
                v.push_back({b[i][j], {i,j}});
            }
        }
        long long ans = -1 * 10e9;
        ans *= 4;
        sort(v.begin(), v.end());
        reverse(v.begin(), v.end());
        // for(auto it: v) {
        //     cout << it.first << " " << it.second.first << " " << it.second.second << "\n" ;
        // }
        for(int i = 0; i < 3 * b.size(); i++) {
            for(int j = i+1; j <  3 * b.size(); j++) {
                for(int k = j+1; k <  3 * b.size(); k++) {
                    bool x = true;
                    if(v[i].second.first == v[j].second.first)
                        continue;
                    if(v[j].second.first == v[k].second.first)
                        continue;
                    if(v[i].second.first == v[k].second.first)
                        continue;
                    if(v[i].second.second == v[j].second.second)
                        continue;
                    if(v[j].second.second == v[k].second.second)
                        continue;
                    if(v[i].second.second == v[k].second.second)
                        continue;
                    long long temp = v[i].first;
                    temp += v[j].first;
                    temp += v[k].first;
                    ans = max(ans, temp);
                }
            }
        }
        return ans;
    }
};
