class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<pair<long long, pair<int, int>>> v;
        int n = board.size();
        int m = board[0].size();

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                v.push_back({board[i][j], {i, j}});
            }
        }

        sort(v.rbegin(), v.rend());

        long long ans = LLONG_MIN;

        for (int f = 0; f < v.size() - 2; f++) {
            for (int s = f + 1; s < v.size() - 1; s++) {
                if (v[f].second.first != v[s].second.first && v[f].second.second != v[s].second.second) {
                    long long pairSum = v[f].first + v[s].first;
                    int row1 = v[f].second.first;
                    int row2 = v[s].second.first;
                    int col1 = v[f].second.second;
                    int col2 = v[s].second.second;


                    for (int t = s + 1; t < v.size(); t++) {
                        int row3 = v[t].second.first;
                        int col3 = v[t].second.second;

                        if (row3 != row1 && row3 != row2 && col3 != col1 && col3 != col2) {
                            long long totalSum = pairSum + v[t].first;
                            ans = max(ans, totalSum);
                            break;  
                        }
                    }
                }
            }
        }

        return ans;
    }
};
