impl Solution {
    pub fn maximum_value_sum(board: Vec<Vec<i32>>) -> i64 {
        let m = board.len();
        let n = board[0].len();
        let mut max_sum = i64::MIN;
        let mut best_values = vec![vec![i64::MIN; 3]; m];
        let mut best_columns = vec![vec![-1; 3]; m];

        for i in 0..m {
            let mut col_values: Vec<(i64, usize)> = board[i]
                .iter()
                .enumerate()
                .map(|(j, &val)| (val as i64, j))
                .collect();
            col_values.sort_by(|a, b| b.0.cmp(&a.0));

            for k in 0..3.min(n) {
                best_values[i][k] = col_values[k].0;
                best_columns[i][k] = col_values[k].1 as i32;
            }
        }

        for r1 in 0..m {
            for r2 in (r1 + 1)..m {
                for r3 in (r2 + 1)..m {
                    for i in 0..3 {
                        for j in 0..3 {
                            if best_columns[r2][j] == best_columns[r1][i] {
                                continue;
                            }
                            for k in 0..3 {
                                if best_columns[r3][k] == best_columns[r1][i] || best_columns[r3][k] == best_columns[r2][j] {
                                    continue;
                                }

                                let current_sum = best_values[r1][i] + best_values[r2][j] + best_values[r3][k];
                                max_sum = max_sum.max(current_sum);
                            }
                        }
                    }
                }
            }
        }

        max_sum
    }
}
