#define endl "\n"
#define vint vector<int>
#define vll vector<long long>
#define v(x) vector<x>
#define pii pair<int, int>
#define pll pair<long long, long long>
#define sz(x) ((int)(x).size())
#define pb push_back
#define ppb pop_back
#define mp make_pair
#define ff first
#define ss second
#define all(x) (x).begin(), (x).end()
#define rep(i, s, n) for (int i = s; i < n; i++)
#define NO cout << "NO" << endl
#define YES cout << "YES" << endl

typedef long long ll;
class Solution {
public:
    ll getMax(int i,int j,int k,v(v(pll))& vec){
        // i->j->k
        ll sum=vec[i][0].ff;
        ll ans1=LLONG_MIN;
        if(vec[j][0].ss!=vec[i][0].ss){
            if(vec[k][0].ss!=vec[i][0].ss && vec[k][0].ss!=vec[j][0].ss){
                ans1 = max(ans1,sum+vec[j][0].ff + vec[k][0].ff) ;
            }else if(vec[k][1].ss!=vec[i][0].ss && vec[k][1].ss!=vec[j][0].ss){
                ans1 = max(ans1,sum+vec[j][0].ff + vec[k][1].ff) ;
            }else{
                ans1 = max(ans1,sum+vec[j][0].ff + vec[k][2].ff) ;
            }
        }else{
            if(vec[k][0].ss!=vec[i][0].ss && vec[k][0].ss!=vec[j][1].ss){
                ans1 = max(ans1,sum+vec[j][1].ff + vec[k][0].ff) ;
            }else if(vec[k][1].ss!=vec[i][0].ss && vec[k][1].ss!=vec[j][1].ss){
                ans1 = max(ans1,sum+vec[j][1].ff + vec[k][1].ff) ;
            }else{
                ans1 = max(ans1,sum+vec[j][1].ff + vec[k][2].ff) ;
            }
        }
        return ans1;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = sz(board);
        int m = sz(board[0]);
        ll ans=LLONG_MIN;
        v(v(pll)) vec;
        for(int i=0;i<n;i++){
            v(pll) tmp;
            for(int j=0;j<m;j++){
                tmp.pb({board[i][j],j});
            }
            sort(all(tmp));
            reverse(all(tmp));
            vec.pb(tmp);
        }
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    ans = max({ans,getMax(i,j,k,vec),getMax(i,k,j,vec)});
                    ans = max({ans,getMax(j,i,k,vec),getMax(j,k,i,vec)});
                    ans = max({ans,getMax(k,j,i,vec),getMax(k,i,j,vec)});
                }   
            }
        }
        return ans;
    }
};
