class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) 
    {
        int n = board.size();
        int m = board[0].size();
        long long maxSum = LLONG_MIN;

        vector<vector<int>> bestCols(n, vector<int>(3, -1)); 

        for (int r = 0; r < n; ++r) 
        {
            vector<pair<int, int>> colValues; 
            for (int c = 0; c < m; ++c) 
            {
                colValues.push_back({board[r][c], c});
            }
            sort(colValues.rbegin(), colValues.rend()); 

            for (int i = 0; i < 3 && i < m; ++i) 
            {
                bestCols[r][i] = colValues[i].second;
            }
        }


        for (int r1 = 0; r1 < n; ++r1) 
        {
            for (int r2 = r1 + 1; r2 < n; ++r2) 
            {
                for (int r3 = r2 + 1; r3 < n; ++r3) 
                {
                    for (int i = 0; i < 3; ++i) 
                    {
                        int c1 = bestCols[r1][i];
                        if (c1 == -1) continue;

                        for (int j = 0; j < 3; ++j) 
                        {
                            int c2 = bestCols[r2][j];
                            if (c2 == -1 || c2 == c1) continue;

                            for (int k = 0; k < 3; ++k) 
                            {
                                int c3 = bestCols[r3][k];
                                if (c3 == -1 || c3 == c1 || c3 == c2) continue;

                                long long currentSum = static_cast<long long>(board[r1][c1]) +
                                                       static_cast<long long>(board[r2][c2]) +
                                                       static_cast<long long>(board[r3][c3]);
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
