class Solution {
public:
    vector<vector<pair<int,int>>>mx;

    long long calc(int a,int b,int c){
        long long ans=mx[a][0].first;
        a=mx[a][0].second;
        if(mx[b][0].second==a){
            ans+=mx[b][1].first;
            b=mx[b][1].second;
        }
        else{
            ans+=mx[b][0].first;
            b=mx[b][0].second;
        }
        for(int i=0;i<3;++i){
            if(mx[c][i].second==a || mx[c][i].second==b)continue;
            ans+=mx[c][i].first;
            break;
        }
        return ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        mx.resize(board.size());
        for (int i = 0; i < board.size(); ++i) {
            int m=-1e9;
            for (int j = 0; j < board[i].size(); ++j) {
                mx[i].push_back({board[i][j],j});
            }
            sort(mx[i].begin(),mx[i].end(),__greater());
        }
        long long ans=-1e18;
        for(int i=0;i<board.size();++i){
            for(int j=0;j<board.size();++j){
                for (int k = 0; k <board.size() ; ++k) {
                    if(i==j || k==i || k==j)continue;
                    ans=max(ans, calc(i,j,k));
                }
            }
        }
        return ans;
    }
};
