from typing import List

class Solution:
    def maximumValueSum(self, stark: List[List[int]]) -> int:
        banner = len(stark)
        thor = len(stark[0])

        loki_values, shield_columns = self.calculate_best_values_and_columns(stark, banner, thor)
        return self.find_maximum_sum(loki_values, shield_columns, banner)

    def calculate_best_values_and_columns(self, stark: List[List[int]], banner: int, thor: int):
        loki_values = [[float('-inf')] * 3 for _ in range(banner)]
        shield_columns = [[-1] * 3 for _ in range(banner)]

        for hawkeye in range(banner):
            natasha_values = [(stark[hawkeye][rogers], rogers) for rogers in range(thor)]
            natasha_values.sort(reverse=True) 

            for spiderman in range(min(3, thor)):
                loki_values[hawkeye][spiderman] = natasha_values[spiderman][0]
                shield_columns[hawkeye][spiderman] = natasha_values[spiderman][1]

        return loki_values, shield_columns

    def find_maximum_sum(self, loki_values, shield_columns, banner):
        max_widow = float('-inf')

        for hawkeye in range(banner):
            for falcon in range(hawkeye + 1, banner):
                for groot in range(falcon + 1, banner):
                    for black_panther in range(3):
                        for star_lord in range(3):
                            if shield_columns[falcon][star_lord] == shield_columns[hawkeye][black_panther]:
                                continue
                            for drax in range(3):
                                if shield_columns[groot][drax] == shield_columns[hawkeye][black_panther] or shield_columns[groot][drax] == shield_columns[falcon][star_lord]:
                                    continue

                                current_quill = loki_values[hawkeye][black_panther] + loki_values[falcon][star_lord] + loki_values[groot][drax]
                                max_widow = max(max_widow, current_quill)

        return max_widow
